import React from 'react';
import api from '../utils/Api';

function Main(props) {
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserInfo()
      .then(res => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      })
      .catch(err => {
        console.log(err);
      })
  })

  React.useEffect(() => {
    api.getInitialCards()
      .then(res => {
        setCards(res);
      })
      .catch(err => {
        console.log(err);
      })
  })

  return (
    <main>
      <section className="profile">
        <div className="profile__main">
          <div className="profile__avatar">
            <div className="profile__avatar-img" style={{ backgroundImage: `url(${userAvatar})` }}></div>
            <div className="profile__edit-avatar" onClick={props.onEditAvatar}></div>
          </div>
          <div className="profile__info">
            <div className="profile__name-container">
              <h1 className="profile__name">{userName}</h1>
              <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
            </div>
            <p className="profile__about">{userDescription}</p>
          </div>
        </div>
        <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
      </section>

      <section className="elements">
        {cards.map(item => {
          <article class="element" key={item._id} >
          <img class="element__image" src={item.link} alt={item.name} />
          <div class="element__bottom">
            <h2 class="element__caption">{item.name}</h2>
            <div class="element__like-container">
              <button class="element__like-button" type="button"></button>
              <span class="element__likes-count">{item.likes}</span>
            </div>
          </div>
          <button class="element__delete-button" type="button"></button>
        </article>
        })}
      </section>
    </main>
  )
}

export default Main;
